import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import BookForm from './BookForm';
import BookDetails from './BookDetails';
import "./app.css";

class App extends Component {
    render() {
        return (
            <Router>
                <div>
                    <div className="ui fixed inverted menu">
                        <div className="ui container">
                            <Link to="/" className="header item">Home</Link>
                            <Link to="/add-book" className="header item">Add Book</Link>
                            <Link to="/edit-book" className="header item">Edit Book</Link>
                            <Link to="/view-book" className="header item">View Book</Link>
                        </div>
                    </div>

                    <div className="ui main container">
                        <Switch>
                            <Route path="/" exact component={BookForm} />
                            <Route path="/add-book" component={BookForm} />
                            <Route path="/edit-book" component={BookDetails} />
                            <Route path="/view-book" component={BookDetails} />
                        </Switch>
                    </div>
                </div>
            </Router>
        );
    }
}

export default App;
