import React, { useState } from 'react';
import { Form, Button, Header, Message } from 'semantic-ui-react';
import axios from 'axios';

const BookForm = () => {
    // State to hold form data
    const [title, setTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [description, setDescription] = useState('');
    const [publishedYear, setPublishedYear] = useState('');
    const [genre, setGenre] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    // Handle form submission
    const handleSubmit = async (event) => {
        event.preventDefault();

        // Book data to be sent in the request
        const bookData = {
            title,
            author,
            description,
            published_year: publishedYear, // Ensure format matches API expectation
            genre,
        };

        try {
            const response = await axios.post('http://127.0.0.1:8000/api/books', bookData, {
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            // Success message and form reset
            console.log('Book added:', response.data);
            setSuccessMessage('Book successfully added!');
            setTitle('');
            setAuthor('');
            setDescription('');
            setPublishedYear('');
            setGenre('');
            setErrorMessage(''); // Clear previous error messages
        } catch (error) {
            console.error('Error adding book:', error);

            if (error.response && error.response.data) {
                const validationErrors = Object.values(error.response.data.errors || {}).flat();
                setErrorMessage(validationErrors.join(', '));
            } else {
                setErrorMessage('Failed to add book. Please try again.');
            }
        }
    };

    return (
        <div className="form-container">
            <Header as="h2">Add a New Book</Header>
            
            {successMessage && (
                <Message success>
                    <Message.Header>{successMessage}</Message.Header>
                </Message>
            )}
            {errorMessage && (
                <Message error>
                    <Message.Header>{errorMessage}</Message.Header>
                </Message>
            )}
            
            <Form onSubmit={handleSubmit}>
                <Form.Field>
                    <label>Title</label>
                    <input
                        type="text"
                        placeholder="Book Title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        required
                    />
                </Form.Field>
                
                <Form.Field>
                    <label>Author</label>
                    <input
                        type="text"
                        placeholder="Author Name"
                        value={author}
                        onChange={(e) => setAuthor(e.target.value)}
                        required
                    />
                </Form.Field>
                
                <Form.Field>
                    <label>Description</label>
                    <textarea
                        placeholder="Book Description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        required
                    />
                </Form.Field>
                
                <Form.Field>
                    <label>Published Year</label>
                    <input
                        type="number"
                        placeholder="e.g., 2023"
                        value={publishedYear}
                        onChange={(e) => setPublishedYear(e.target.value)}
                        required
                    />
                </Form.Field>
                
                <Form.Field>
                    <label>Genre</label>
                    <input
                        type="text"
                        placeholder="Book Genre"
                        value={genre}
                        onChange={(e) => setGenre(e.target.value)}
                        required
                    />
                </Form.Field>
                
                <Button type="submit" primary>
                    Submit
                </Button>
            </Form>
        </div>
    );
};

export default BookForm;
